  <h1>Login Page</h1><br/>
   <MDBContainer className="p-3 my-5 d-flex flex-column w-50">

<MDBInput wrapperClass='mb-4' label='Email address' id='form1' type='email' onChange={e=>setUser(e.target.value)}/>
