import React from 'react'
import { useState } from 'react'
import axios from "axios"
import {useNavigate} from "react-router-dom"
import {
  MDBContainer,
  MDBInput,
  MDBCheckbox,
  MDBBtn,
  MDBIcon
}
from 'mdb-react-ui-kit';
const LoginPage = ()=>{
  const [FirstName,setFirstName]=useState()
  const [LastName,setLastName]=useState()
  const [password,setPassword]=useState()
  const navigate=useNavigate()
  const login = async ()=>{
      const {data}= await axios.get("http://localhost:8000/users")
      let user = data.find(user =>{
          return user.Email==FirstName&&user.Password==password
      })
      if(user){
          navigate("/home")
        // console.log("yesss")
      }
      else{
          navigate("register")
        // console.log("noooo")
      }
  }
  const newRegister =()=>{
    navigate("/register")
  }
  return(<div>
  <h1>Login Page</h1><br/>
   <MDBContainer className="p-3 my-5 d-flex flex-column w-50">

<MDBInput wrapperClass='mb-4' label='Email address' id='form1' type='email'/>
<MDBInput wrapperClass='mb-4' label='Password' id='form2' type='password'/>

<div className="d-flex justify-content-between mx-3 mb-4">
  <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Remember me' />
  <a href="!#">Forgot password?</a>
</div>

<MDBBtn className="mb-4">Sign in</MDBBtn>

<div className="text-center">
  <p>Not a member? <a href="#!">Register</a></p>
  <p>or sign up with:</p>
    Email<input type={"email"} onChange={(e)=>setLastName(e.target.value)}/><br/>
    Password:<input type={"text"}onChange={(e)=>setPassword(e.target.value)}/><br/>
    <button onClick={login} >Login</button>
    <button onClick={newRegister} >New Register</button>
  </div>
  </MDBContainer>
  </div>
   
 
 
  )
}
export default LoginPage;
